    const binaryBuffer = Buffer.from(passportData.content, 'base64');

    item.binary = item.binary || {};
    item.binary.passport = {
      data: binaryBuffer,
      mimeType: passportData.mimeType || 'application/octet-stream',
      fileName: passportData.filename || 'passport'
